import { Component, OnInit } from '@angular/core';
import { MatDialog, MatDialogConfig } from '@angular/material/dialog';
import { User } from 'src/app/shared/interface/user';
import { TaskService } from 'src/app/shared/services/task.service';
import { AddTaskComponent } from '../add-task/add-task.component';
@Component({
  selector: 'app-user-home',
  templateUrl: './user-home.component.html',
  styleUrls: ['./user-home.component.css'],
})
export class UserHomeComponent implements OnInit {
  constructor(public dialog: MatDialog, private taskservice: TaskService) {}
  data!: User;
  user!: User[];
  task: any;
  ngOnInit(): void {}
  openFormAdd() {
    const dialogConfig = new MatDialogConfig();
    dialogConfig.disableClose = false;
    dialogConfig.width = '40%';
    dialogConfig.height = '350px';
    dialogConfig.panelClass = 'add-task-modal';
    this.dialog.open(AddTaskComponent, dialogConfig);
  }
  public getTasks(): void {
    this.taskservice.GetTaskByEmail().subscribe((data: User) => {
      this.task = data.tasks;
      console.log(this.task);
      this.ngOnInit();
    });
  }
}
<app-sider-bar-user></app-sider-bar-user>
<div class="mt-3 mb-3 inputs" id="add_demande">
  <button
    mat-button
    class="btn btn-primary rounded-pill"
    (click)="openFormAdd()"
  >
    <i class="fas fa-add fa-fw me-1"></i>Add Task
  </button>
</div>
<table class="table align-middle mb-0 bg-white">
  <thead class="bg-light">
    <tr>
      <th>Description</th>
      <th>Date</th>
      <th>Status</th>
      <th>Actions</th>
    </tr>
  </thead>
  <tbody *ngFor="let tasks of task">
    <tr>
      <td>
        <p class="fw-normal mb-1">{{ tasks["description"] }}</p>
      </td>
      <td>
        <p class="fw-normal mb-1">{{ tasks["created_date"] }}</p>
      </td>
      <td>
        <span class="badge badge-success rounded-pill d-inline">{{
          tasks["status"]
        }}</span>
      </td>
      <td>
        <a href="#!" data-mdb-toggle="tooltip" title="Done"
          ><i class="fas fa-edit text-update me-3"></i
        ></a>
        <a href="#!" data-mdb-toggle="tooltip" title="Remove"
          ><i class="fas fa-trash-alt text-danger"></i
        ></a>
      </td>
    </tr>
  </tbody>
</table>
