import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatDialogRef } from '@angular/material/dialog';
import { ToastrService } from 'ngx-toastr';
import { Task } from 'src/app/shared/interface/task';
import { TaskService } from 'src/app/shared/services/task.service';

@Component({
  selector: 'app-add-task',
  templateUrl: './add-task.component.html',
  styleUrls: ['./add-task.component.css'],
})
export class AddTaskComponent implements OnInit {
  now = new Date();
  formattedDate = `${this.now.getFullYear()}-${(this.now.getMonth() + 1)
    .toString()
    .padStart(2, '0')}-${this.now.getDate().toString().padStart(2, '0')}`;
  task!: Task[];
  formtask!: FormGroup;
  description!: string;
  date!: string;
  status!: string;
  constructor(
    public dialogRef: MatDialogRef<AddTaskComponent>,
    private fb: FormBuilder,
    private taskservices: TaskService,
    private toastr: ToastrService
  ) {}

  ngOnInit(): void {
    this.formtask = this.fb.group({
      description: ['', [Validators.required]],
      date: ['', [Validators.required]],
      status: ['Still in Progress', [Validators.required]],
      email: [localStorage.getItem('email'), [Validators.required]],
    });
  }
  public addtask(): void {
    let e = this.formtask.value;
    let eToSave: Task = {
      description: e.description,
      created_date: e.date,
      status: e.status,
      email: e.email,
    };
    this.taskservices.addTask(eToSave).subscribe(() => {
      this.toastr.success('Task ADDED succesful');
      this.closeDialog();
      window.location.reload();
    });
  }
  closeDialog() {
    this.dialogRef.close();
  }
}
<form class="mt-4 me-5 ms-5" [formGroup]="formtask">
  <!-- 2 column grid layout with text inputs for the first and last names -->
  <div class="form-check d-flex justify-content-center">
    <h3>Add Task</h3>
  </div>
  <div class="mb-4">
    <label class="form-label"> Add Description</label>
    <input
      type="text"
      class="form-control"
      placeholder="Task Description..."
      formControlName="description"
    />
  </div>

  <div class="row">
    <!-- Email input -->
    <div class="col-5 me-4">
      <div class="mb-4">
        <label class="form-label" for="form3Example3">Status</label>
        <select class="form-select" aria-label="Default select example">
          <option selected value="Still in Progress">Still in Progress</option>
        </select>
      </div>
    </div>
    <!-- Password input -->
    <div class="col-5 ms-4">
      <div class="mb-4">
        <label class="form-label" for="form3Example3">Creation_Date</label>
        <input
          type="date"
          id="start"
          class="form-control"
          name="trip-start"
          min="{{ formattedDate }}"
          formControlName="date"
        />
      </div>
    </div>
  </div>
  <!-- Checkbox -->
  <div class="form-check d-flex justify-content-center mt-4 mb-2">
    <button class="btn btn-danger me-4" (click)="closeDialog()">Cancel</button>
    <button
      type="submit"
      class="btn btn-primary"
      [disabled]="formtask.invalid"
      (click)="addtask()"
    >
      Add Task
    </button>
  </div>

  <!-- Submit button -->

  <!-- Register buttons -->
</form>
